import React, { useState } from 'react';
import axios from 'axios';

export default function Register() {

    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
  
    const handleSubmit = async (e) => {
      e.preventDefault();
      try {
        const response = await axios.post('http://localhost:5000/register', { username, email, password });
        console.log(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    return (
        <div class="page-wrapper">
        <div class="container">
            <h2>Регистрация</h2>
            <form>
                <div class="form-group">
                    <label for="username" class="form-label">Имя пользователя</label>
                    <input type="text" id="username" name="username" class="form-input" required />
                </div>
                <div class="form-group">
                    <label for="email" class="form-label">Электронная почта</label>
                    <input type="email" id="email" name="email" class="form-input" required />
                </div>
                <div class="form-group">
                    <label for="password" class="form-label">Пароль</label>
                    <input type="password" id="password" name="password" class="form-input" required />
                </div>
                <div class="form-group">
                    <label for="confirm-password" class="form-label">Подтвердите пароль</label>
                    <input type="password" id="confirm-password" name="confirm-password" class="form-input" required />
                </div>
                <input type="submit" value="Регистрация" class="form-submit" />
            </form>

        </div>
    </div>
    )
}
