import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { format } from 'date-fns';
import { ru } from 'date-fns/locale';
import Header from "../main/header";
import Footer from "../main/footer";

const Profile = () => {
    const [profileData, setProfileData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchProfile = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                navigate('/login');
                return;
            }

            try {
                const response = await axios.get('http://localhost:5000/profile', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                console.log('Profile data:', response.data);
                setProfileData(response.data);
            } catch (error) {
                console.error('Profile fetch error:', error);
                setError('Ошибка при загрузке профиля. Пожалуйста, войдите снова.');
                localStorage.removeItem('token');
                navigate('/login');
            } finally {
                setLoading(false);
            }
        };

        fetchProfile();
    }, [navigate]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    const formatDate = (dateString) => {
        return format(new Date(dateString), 'dd MMMM yyyy', { locale: ru });
    };

    return (
        <>
            <Header />
            <main>
                <div className="profile-section">
                    <div className="profile-content">
                        <h1>Профиль пользователя</h1>
                        <div className="profile-details">
                            <img src="./img/user-avatar.jpeg" alt="User Avatar" className="profile-avatar" />
                            <div className="user-info">
                                <p>Имя пользователя: {profileData.username}</p>
                                <p>Email: {profileData.email}</p>
                                <p>Дата регистрации: {formatDate(profileData.registrationDate)}</p>
                            </div>
                        </div>
                        <div className="order-history">
                            <h2>История заказов</h2>
                            <ul className="order-list">
                                {profileData.orders.map((order, index) => (
                                    <li key={index}>{`Заказ #${order.id}: ${order.product} - ${order.price} ₽ - ${formatDate(order.date)}`}</li>
                                ))}
                            </ul>
                        </div>
                        <a href="/edit-profile" className="btn">Редактировать профиль</a>
                    </div>
                </div>
            </main>
            <Footer />
        </>
    );
};
