export default function ProductCard() {

    const [quantity, setQuantity] = useState(1);

    const handlePurchase = () => {
        alert(`Вы купили ${quantity} шт. RTX 4090 на сумму ${quantity * 149000} ₽`);
    };

    return (
        <div className="purchase-container">
            <div className="card">
                <img src={videocardImage} alt="Videocard" />
                <h3>RTX 4090</h3>
                <p>Самая мощная видеокарта в мире</p>
                <p className="price">149 000 ₽</p>
                <div className="quantity-container">
                    <label htmlFor="quantity">Количество:</label>
                    <input
                        type="number"
                        id="quantity"
                        name="quantity"
                        min="1"
                        value={quantity}
                        onChange={(e) => setQuantity(e.target.value)}
                    />
                </div>
                <button className="btn" onClick={handlePurchase}>Купить сейчас</button>
            </div>
        </div>
    )
}
