import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Header from "../main/header";
import Footer from "../main/footer";
import { format } from 'date-fns';
import { ru } from 'date-fns/locale';

export default function Profile() {
    const [profileData, setProfileData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchProfile = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                navigate('/login');
                return;
            }

            try {
                const response = await axios.get('http://localhost:5000/profile', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                console.log('Profile data:', response.data);

                // Сохраняем информацию о пользователе в localStorage
                localStorage.setItem('user', JSON.stringify(response.data));

                setProfileData(response.data);
            } catch (error) {
                console.error('Profile fetch error:', error);
                setError('Ошибка при загрузке профиля. Пожалуйста, войдите снова.');
                localStorage.removeItem('token');
                navigate('/login');
            } finally {
                setLoading(false);
            }
        };

        fetchProfile();
    }, [navigate]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <>
            <Header />
            <main>
                <div className="profile-section">
                    <div className="profile-content">
                        <h1>Профиль пользователя</h1>
                        <div className="profile-details">
                            <img src={require('../img/profile_avatar.jpg')} alt="User Avatar" className="profile-avatar" />
                            <div className="user-info">
                                <p>Имя пользователя: {profileData.username}</p>
                                <p>Email: {profileData.email}</p>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="order-history-container">
                    <h1 className="order-history-title">История заказов</h1>
                    <table className="order-history-table">
                        <thead>
                            <tr>
                                <th>Order ID</th>
                                <th>Date</th>
                                <th>Items</th>
                                <th>Total</th>
                                <th>Status</th>
                            </tr>
                        </thead>
                        <tbody>
                            {profileData.orders && profileData.orders.map((order, index) => (
                                <tr key={index}>
                                    <td>{order.id}</td>
                                    <td>{order.items.join(', ')}</td>
                                    <td>{order.total}</td>
                                    <td>{order.status}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </main>
            <Footer />
        </>
    );
};
