import React, { useState } from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

export default function Main(props) {
    const [productName, setProductName] = useState('');
    const [productSubtitle, setProductSubtitle] = useState('');
    const [price, setPrice] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        const product = { productName, productSubtitle, price };

        const response = await fetch('/api/products', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(product),
        });

        if (response.ok) {
            console.log('Product added successfully');
        } else {
            console.error('Error adding product');
        }
    };

    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1,
        centerPadding: '30px',
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 1,
                    infinite: true,
                    dots: true
                }
            },
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1
                }
            }
        ]
    };

    return (
        <main>
            <div className="intro-section">
                <div className="intro-content">
                    <h1>{props.Welcome}</h1>
                    <p>{props.subtitleWelcome}</p>
                </div>
            </div>
            <div className="products">
                <h2>{props.titleProduct}</h2>
                <Slider {...settings}>
                    {/* Карточки продуктов здесь */}
                </Slider>
            </div>
            <div className="about-us">
                <div className="about-content">
                    <h2>{props.titleAboutUs}</h2>
                    <p>{props.subtitletitleAboutUs1}</p>
                    <p>{props.subtitletitleAboutUs2}</p>
                    <p>{props.subtitletitleAboutUs3}</p>
                </div>
            </div>
            <div className="news">
                <div className="news-content">
                    <h2>{props.titleNews}</h2>
                    <p className="description">{props.subtitleNews}</p>
                    <Slider {...settings}>
                        {/* Карточки новостей здесь */}
                    </Slider>
                </div>
            </div>
            <div className="product-form">
                <h2>Add New Product</h2>
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        placeholder="Product Name"
                        value={productName}
                        onChange={(e) => setProductName(e.target.value)}
                        required
                    />
                    <input
                        type="text"
                        placeholder="Product Subtitle"
                        value={productSubtitle}
                        onChange={(e) => setProductSubtitle(e.target.value)}
                        required
                    />
                    <input
                        type="text"
                        placeholder="Price"
                        value={price}
                        onChange={(e) => setPrice(e.target.value)}
                        required
                    />
                    <button type="submit">Add Product</button>
                </form>
            </div>
        </main>
    );
}
