import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Header from '../main/header';
import Footer from '../main/footer';

export default function ProductCard() {
    const { productId } = useParams(); // Получение параметра productId из URL
    const [product, setProduct] = useState(null);

    useEffect(() => {
        // Замените это на реальный запрос к серверу для получения данных о продукте
        const fetchProduct = async () => {
            const response = await fetch(`http://localhost:5000/products/${productId}`);
            const data = await response.json();
            setProduct(data);
        };

        fetchProduct();
    }, [productId]);

    const handleAddToCart = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            alert('No token found. Please log in first.');
            return;
        }

        try {
            const response = await fetch('http://localhost:5000/cart', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`,
                },
                body: JSON.stringify({
                    productId: product.id,
                    quantity: 1,
                }),
            });

            if (response.ok) {
                alert('Product successfully added to cart');
                const storedCartItems = localStorage.getItem('cartItems');
                const cartItems = storedCartItems ? JSON.parse(storedCartItems) : [];
                cartItems.push(product);
                localStorage.setItem('cartItems', JSON.stringify(cartItems));
            } else {
                const errorData = await response.json();
                alert('Error adding product to cart: ' + errorData.error);
            }
        } catch (error) {
            alert('Error adding product to cart: ' + error.message);
        }
    };

    if (!product) {
        return <div>Loading...</div>;
    }

    return (
        <>
            <Header />
            <div className="product-container">
                <div className="product-page">
                    <div className="product-gallery">
                        <img src={product.image} alt={product.name} />
                        <div className="product-thumbnails">
                            {/* Добавьте миниатюры изображений продукта, если они есть */}
                        </div>
                    </div>
                    <div className="product-details">
                        <h2>{product.name}</h2>
                        <p className="product-price">{product.price} ₽</p>
                        <p className="product-availability">В наличии</p>
                        <p className="product-description">{product.description}</p>
                        <button className="product-button" onClick={handleAddToCart}>Добавить в корзину</button>
                    </div>
                </div>
            </div>
            <Footer />
        </>
    );
}
