import React, { useState } from 'react';

export default function ProductCard() {

    const [quantity, setQuantity] = useState(1);

    const handlePurchase = () => {
        alert(`Вы купили ${quantity} шт. RTX 4090 на сумму ${quantity * 149000} ₽`);
    };

    return (
        <div className="purchase-container">
        <div className="product-details">
            <img src={require('../img/videocard.jpeg')} alt="Videocard" className="main-image" />
            <h1>RTX 4090</h1>
            <p>Самая мощная видеокарта в мире</p>
            <p className="price">149 000 ₽</p>
            <button className="btn" onClick={handlePurchase}>Купить сейчас</button>
        </div>
        <div className="additional-info">
            <h2>Как и когда получить товар</h2>
            <ul>
                <li>Доставка</li>
                <li>Самовывоз доступен из 2 магазинов</li>
                <li>Сейчас в наличии: в 2 магазинах</li>
            </ul>
            <div className="actions">
                <button className="btn-compare">Добавить в сравнение</button>
                <button className="btn-favorites">Добавить в избранное</button>
            </div>
        </div>
    </div>
    )
}
