import Header from "./header"
import Main from "./main"
import Footer from "./footer"
import { useEffect, useState } from 'react';

export default function MainPage() {

    const [backendData, setBackendData] = useState([]);
  
    useEffect(() => {
      fetch('http://localhost:8080/server')
        .then(res => res.json())
        .then(data => {
          setBackendData(data);
        })
        .catch(error => {
          console.error('Error fetching data:', error);
        });
    }, []);
  
    const settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 3,
      slidesToScroll: 1,
      responsive: [
          {
              breakpoint: 1024,
              settings: {
                  slidesToShow: 2,
                  slidesToScroll: 1,
                  infinite: true,
                  dots: true
              }
          },
          {
              breakpoint: 600,
              settings: {
                  slidesToShow: 1,
                  slidesToScroll: 1
              }
          }
      ]
  };

    return (
        <>
            <Header />
            <Main data={backendData}/>
            <Footer />
        </>
    )
}
