import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Header from '../main/header';
import Footer from '../main/footer';

const productData = {
    1: {
        mainImageSrc: require('../img/videocard.jpeg'),
        images: [require('../img/videocard.jpeg'), require('../img/ozu.jpeg'), require('../img/proccessor.jpeg'), require('../img/profile_avatar.jpg')],
        title: 'RTX 4090',
        price: '149 000 ₽',
        availability: 'Базовая',
        description: 'NVIDIA GeForce RTX 4090 - это одна из самых мощных графических карт, выпущенных NVIDIA в серии RTX 40. Эта видеокарта предназначена для высокопроизводительных игр, профессиональной работы с графикой и машинного обучения. Она основана на архитектуре Ada Lovelace и использует самые современные технологии, чтобы предоставить исключительную производительность и визуальное качество.'
    },
    2: {
        mainImageSrc: require('../img/proccessor.jpeg'),
        images: [require('../img/proccessor.jpeg'), require('../img/ozu.jpeg'), require('../img/videocard.jpeg'), require('../img/profile_avatar.jpg')],
        title: 'Ryzen 9 5900X',
        price: '65 000 ₽',
        availability: 'В наличии',
        description: 'AMD Ryzen 9 5900X - мощный процессор для высокопроизводительных задач и игр. Построен на архитектуре Zen 3 и предлагает высокую производительность и энергоэффективность.'
    },
    3: {
        mainImageSrc: require('../img/ozu.jpeg'),
        images: [require('../img/ozu.jpeg'), require('../img/videocard.jpeg'), require('../img/proccessor.jpeg'), require('../img/profile_avatar.jpg')],
        title: 'Trident Z RGB',
        price: '20 000 ₽',
        availability: 'В наличии',
        description: 'Оперативная память Trident Z RGB от G.SKILL - это высокопроизводительная память с яркой RGB подсветкой, предназначенная для энтузиастов и геймеров.'
    }
};

export default function ProductCard() {
    const { productId } = useParams();
    const [product, setProduct] = useState(null);
    const [mainImage, setMainImage] = useState('');

    useEffect(() => {
        // Здесь можно сделать запрос на сервер для получения данных о продукте
        // Например: fetchProductData(productId)
        const product = productData[productId];
        if (product) {
            setProduct(product);
            setMainImage(product.mainImageSrc);
        }
    }, [productId]);

    const handleThumbnailClick = (image) => {
        setMainImage(image);
    };

    const handleAddToCart = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            alert('No token found. Please log in first.');
            return;
        }

        try {
            const response = await fetch('http://localhost:5000/cart', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`,
                },
                body: JSON.stringify({
                    productId: productId,
                    quantity: 1,
                }),
            });

            if (response.ok) {
                alert('Product successfully added to cart');
            } else {
                const errorData = await response.json();
                alert('Error adding product to cart: ' + errorData.error);
            }
        } catch (error) {
            alert('Error adding product to cart: ' + error.message);
        }
    };

    if (!product) {
        return <div>Loading...</div>;
    }

    return (
        <>
            <Header />
            <div className="product-container">
                <div className="product-page">
                    <div className="product-gallery">
                        <img src={mainImage} alt={product.title} />
                        <div className="product-thumbnails">
                            {product.images.map((image, index) => (
                                <img
                                    key={index}
                                    src={image}
                                    alt={`${product.title} photo${index + 1}`}
                                    onClick={() => handleThumbnailClick(image)}
                                />
                            ))}
                        </div>
                    </div>
                    <div className="product-details">
                        <h2>{product.title}</h2>
                        <p className="product-price">{product.price}</p>
                        <p className="product-availability">{product.availability}</p>
                        <p className="product-description">{product.description}</p>
                        <button className="product-button" onClick={handleAddToCart}>Добавить в корзину</button>
                    </div>
                </div>
            </div>
            <Footer />
        </>
    );
}
