import React, { useState } from 'react';
import axios from 'axios';

export default function Register() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/register', { username, email, password });
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
      <div className="registration-container">
          <h1 className="registration-title">Register</h1>
          <form className="registration-form">
              <div className="form-group">
                  <label htmlFor="username" className="form-label">Username</label>
                  <input type="text" id="username" className="form-input" name="username" required/>
              </div>
              <div className="form-group">
                  <label htmlFor="email" className="form-label">Email</label>
                  <input type="email" id="email" className="form-input" name="email" required/>
              </div>
              <div className="form-group">
                  <label htmlFor="password" className="form-label">Password</label>
                  <input type="password" id="password" className="form-input" name="password" required/>
              </div>
              <div className="form-group">
                  <label htmlFor="confirm-password" className="form-label">Confirm Password</label>
                  <input type="password" id="confirm-password" className="form-input" name="confirm-password" required/>
              </div>
              <button type="submit" className="form-button">Register</button>
          </form>
      </div>
  );
};
